import dayjs from 'dayjs';
import isBetween from 'dayjs/plugin/isBetween';

import ListGroup from 'react-bootstrap/ListGroup';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Dropdown from 'react-bootstrap/Dropdown';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';

import PeopleFill from 'react-bootstrap-icons/dist/icons/people-fill';
import ThreeDotsVertical from 'react-bootstrap-icons/dist/icons/three-dots-vertical';
import Trash2Fill from 'react-bootstrap-icons/dist/icons/trash2-fill';
import PencilSquare from 'react-bootstrap-icons/dist/icons/pencil-square';
import PlusCircleFill from 'react-bootstrap-icons/dist/icons/plus-circle-fill';

import React, { useState } from 'react';

dayjs.extend(isBetween);

function Task(props) {
    return (
        <ListGroup.Item className="d-flex align-items-center">
            <Col as="span" className={props.isUrgent && "text-danger font-weight-bold"}>
                <Form.Check
                    custom
                    type="checkbox"
                    id={"custom-checkbox-" + props.id}
                    label={props.description}
                />
            </Col>
            <Col as="span" className="text-dark text-center">{props.isPrivate && <PeopleFill />}</Col>
            <Col as="span" className="font-075 text-right d-flex justify-content-end">
                <span>{props.date.format("dddd D MMMM YYYY [at] H:mm")}</span>
                <TaskControls taskID={props.id} deleteTask={props.deleteTask} />
            </Col>
        </ListGroup.Item>
    );
}

function TaskControls(props) {
    const CustomToggle = React.forwardRef(({ children, onClick }, ref) => (
        <a
            href=""
            ref={ref}
            onClick={(e) => {
                e.preventDefault();
                onClick(e);
            }}
        >
            {children}
        </a>
    ));

    // forwardRef again here!
    // Dropdown needs access to the DOM of the Menu to measure it
    const CustomMenu = React.forwardRef(
        ({ children, style, className, 'aria-labelledby': labeledBy }, ref) => {
            const [value, setValue] = useState('');

            return (
                <div
                    ref={ref}
                    style={style}
                    className={className}
                    aria-labelledby={labeledBy}
                >
                    <ul className="list-unstyled m-0">
                        {React.Children.toArray(children).filter(
                            (child) =>
                                !value || child.props.children.toLowerCase().startsWith(value),
                        )}
                    </ul>
                </div>
            );
        },
    );
    return (
        <Dropdown className="d-inline ml-2">
            <Dropdown.Toggle as={CustomToggle} id="dropdown-custom-components">
                <ThreeDotsVertical color="" size={18} />
            </Dropdown.Toggle>

            <Dropdown.Menu className="min-width-5" as={CustomMenu}>
                <Dropdown.Item className="px-3"><PencilSquare className="mr-3" size={16} />Edit</Dropdown.Item>
                <Dropdown.Item className="px-3" onClick={() => props.deleteTask(props.taskID)}><Trash2Fill className="mr-3" size={16} />Delete</Dropdown.Item>
            </Dropdown.Menu>
        </Dropdown>
    );
}

function TaskList(props) {
    let newList = applyFilter(props.activeFilter, props.tasks);

    const deleteTask = (tskID) => {
        props.setTasks(tsks => tsks.filter(t => t.id != tskID));
    }

    const taskList = [...newList].map(task => <Task key={task.id} {...task} deleteTask={deleteTask} />);
    return (
        <ListGroup variant="flush" className="margin-b-75">
            {taskList}
        </ListGroup>
    );
}

const applyFilter = (selected, list) => {
    switch (selected) {
        case 2:
            return (list.filter(task => task.isUrgent));
        case 3:
            return (list.filter(task => {
                if (dayjs(task.date).format("YYYY-MM-DD") === dayjs().format("YYYY-MM-DD")) return true;
                else return false;
            }));
        case 4:
            return (list.filter(task => {
                if (dayjs(task.date).isBetween(dayjs().startOf('d').add(1, 'day'), dayjs().startOf('d').add(8, 'day'))) return true;
                else return false;
            }));
        case 5:
            return (list.filter(task => task.isPrivate));
        default:
            return list;
    }
}

function TaskAdder(props) {
    const [show, setShow] = useState(false);
    const [description, setDescription] = useState('');
    const [isPrivate, setIsPrivate] = useState(false);
    const [isImportant, setIsImportant] = useState(false);
    const [date, setDate] = useState(dayjs());
    const [validated, setValidated] = useState(false);

    const formProps = {
        description: description, isPrivate: isPrivate, isImportant: isImportant, date: date, validated: validated,
        setDescription: setDescription, setIsPrivate: setIsPrivate, setIsImportant: setIsImportant, setDate: setDate
    };

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const handleSumbit = (event) => {
        event.preventDefault();
        const id = props.tasks.reduce((prev, current) => (prev.id > current.id) ? prev.id : current.id) + 1;
        const task = { id: id, description: description, isPrivate: isPrivate, isUrgent: isImportant, date: date };

        if (description != '') {
            setValidated(true);
            props.setTasks(tsks => [...tsks, task]);
            handleClose();
        }
    };

    return (
        <>
            <PlusCircleFill color="#17a2b8" size={64} onClick={handleShow} className="cursor-pointer" />
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Add new task</Modal.Title>
                </Modal.Header>

                <Modal.Body>
                    <TaskForm  {...formProps} />
                </Modal.Body>

                <Modal.Footer>
                    <Button variant="info" onClick={handleSumbit}>Confirm</Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}

function TaskForm(props) {
    return (
        <Form noValidate validated={props.validated} onSubmit={props.handleSubmit}>
            <Form.Group controlId="formDescription">
                <Form.Label>Task description</Form.Label>
                <Form.Control type="text" placeholder="Enter a description for your task"
                    onChange={(ev) => props.setDescription(ev.target.value)}
                />
                <Form.Control.Feedback type="invalid">
                    Please add a description.
                </Form.Control.Feedback>
            </Form.Group>
            <Form.Group className="d-flex" controlId="formCheckboxes">
                <Form.Check
                    custom
                    type="checkbox"
                    id="form-checkbox-1"
                    label="Private"
                    className="mr-3"
                    onChange={() => props.setIsPrivate(!props.isPrivate)}
                />
                <Form.Check
                    custom
                    type="checkbox"
                    id="form-checkbox-2"
                    label="Important"
                    onChange={() => props.setIsImportant(!props.isImportant)}
                />
            </Form.Group>
            <Form.Group>
                <Form.Label>Deadline</Form.Label>
                <Form.Control type="date" onChange={(ev) => props.setDate(dayjs(ev.target.value))} />
            </Form.Group>
            <Button type="submit">Submit</Button>
        </Form>
    );
}

export { TaskList, TaskAdder };
