import { Collapse } from 'bootstrap';
import {Modal, Form, Col, ListGroup, Button, InputGroup} from 'react-bootstrap';
import {PlusCircleFill, Check, X, Clock, Calendar} from 'react-bootstrap-icons';

import TimeField from 'react-simple-timefield';

function TaskAdder(props) {
    const { show, handleShow, handleClose, clearForm, ...propsObj } = props;    

    return (
        <>
            <PlusCircleFill color="#17a2b8" size={64} onClick={handleShow} className="cursor-pointer hover-scaleup" />
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Add new task</Modal.Title>
                </Modal.Header>

                <Modal.Body>
                    <TaskForm  {...propsObj} />
                </Modal.Body>

                <Modal.Footer>
                    <Button variant="link" className="text-dark" onClick={clearForm}>Clear</Button>
                    <Button variant="info" onClick={props.handleSubmit}>Confirm</Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}

function TaskForm(props) {
    const formDescription = <Form.Control type="text" placeholder="Enter a description for your task"
        onChange={(ev) => props.handleDescChange(ev)}
        isInvalid={!props.validated ? false : !props.description.isValid}
        isValid={!props.validated ? false : props.description.isValid}
        value={props.description.value}
    />

    const formImportant = <Form.Check
        custom
        type="checkbox"
        id="form-checkbox-1"
        label="Important"
        onChange={(ev) => props.setIsImportant(ev.target.checked)}
        checked={props.isImportant}
    />

    const formPrivate = <Form.Check
        custom
        type="checkbox"
        id="form-checkbox-2"
        label="Private"
        className="mr-3"
        onChange={(ev) => props.setIsPrivate(ev.target.checked)}
        checked={props.isPrivate}
    />

    const formDeadline = <InputGroup>
        <InputGroup.Prepend>
            <InputGroup.Text>
                <Calendar />
            </InputGroup.Text>
        </InputGroup.Prepend>
        <Form.Control type="date" onChange={(ev) => props.handleDateChange(ev)}
            isInvalid={!props.validated ? false : !props.date.isValid}
            isValid={!props.validated ? false : props.date.isValid}
            value={props.date.value}
        />
    </InputGroup>

    const formTimeField = <TimeField input={
        <InputGroup>
            <InputGroup.Prepend>
                <InputGroup.Text>
                    <Clock />
                </InputGroup.Text>
            </InputGroup.Prepend>
            <Form.Control />
        </InputGroup>
    } />
    
    return (
        props.editMode < 0 ?
            <Form noValidate onSubmit={props.handleSubmit}>
                <Form.Group controlId="formDescription">
                    <Form.Label>Task description</Form.Label>
                    {formDescription}
                    <Form.Control.Feedback type="invalid">
                        Please add a description.
                    </Form.Control.Feedback>
                </Form.Group>
                <Form.Group className="d-flex py-2" controlId="formCheckboxes">
                    {formPrivate} {formImportant}
                </Form.Group>
                <Form.Label>Deadline</Form.Label>
                <Form.Group className="d-lg-flex">
                    <Col lg={8} className="p-0 pr-lg-3 mb-2">
                        {formDeadline}
                        <Form.Control.Feedback type="invalid">
                            Deadline should be after current time.
                        </Form.Control.Feedback>
                    </Col>
                    <Col lg={4} className="p-0">
                        {formTimeField}
                    </Col>                   
                </Form.Group>
            </Form>
            :
            <ListGroup.Item className="edit-animation">
                <Form noValidate onSubmit={props.handleSubmit} className="d-flex align-items-center flex-column flex-md-row">
                    <Col lg={3} as="span">
                        {formDescription}
                    </Col>
                    <Col lg={3} as="span" className="d-flex justify-content-center my-3 my-lg-0">
                        {formPrivate} {formImportant}
                    </Col>
                    <Col lg={3}>
                        {formDeadline}
                        {/* <Form.Control type="date" onChange={(ev) => props.handleDateChange(ev)}
                            isInvalid={!props.validated ? false : !props.date.isValid}
                            isValid={!props.validated ? false : props.date.isValid}
                            value={props.date.value}
                        /> */}
                    </Col>
                    <Col lg={3} as="span" className="text-right d-flex justify-content-end align-items-center">   
                        {formTimeField}                     
                        {/* <TimeField input={
                                <Form.Control />
                        } /> */}
                        <div className="d-flex align-items-center ml-3">
                            <X size={32} className="hover-scaleup cursor-pointer" onClick={() => props.editTask(-1)} />
                            <Check size={40} color="#17a2b8" className="hover-scaleup cursor-pointer" onClick={props.handleSubmit} />
                        </div>                        
                    </Col>
                </Form>
            </ListGroup.Item>
    );

    /* <ListGroup.Item className="edit-animation">
                <Form noValidate onSubmit={props.handleSubmit} className="d-flex align-items-center flex-column flex-md-row">
                    <Col lg={4} as="span">
                        {formDescription}
                    </Col>
                    <Col lg={2} as="span" className="d-flex justify-content-center flex-column my-3 my-lg-0">
                        {formPrivate} {formImportant}
                    </Col>
                    <Col lg={2} as="span" className="my-3 my-lg-0">
                        {formTimeField}
                    </Col>
                    <Col lg={4} as="span" className="font-075 text-right d-flex justify-content-end align-items-center">
                        {formDeadline}
                        <X size={40} className="hover-scaleup cursor-pointer ml-3" onClick={() => props.editTask(-1)} />
                        <Check size={48} color="#17a2b8" className="hover-scaleup cursor-pointer" onClick={props.handleSubmit} />
                    </Col>
                </Form>
            </ListGroup.Item> */
}

export {TaskAdder, TaskForm};
