import dayjs from 'dayjs';
import isBetween from 'dayjs/plugin/isBetween';

import ListGroup from 'react-bootstrap/ListGroup';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Dropdown from 'react-bootstrap/Dropdown';

import PersonFill from 'react-bootstrap-icons/dist/icons/person-fill';
import ThreeDotsVertical from 'react-bootstrap-icons/dist/icons/three-dots-vertical';
import Trash2Fill from 'react-bootstrap-icons/dist/icons/trash2-fill';
import PencilSquare from 'react-bootstrap-icons/dist/icons/pencil-square';
import Check2 from 'react-bootstrap-icons/dist/icons/check2';
import X from 'react-bootstrap-icons/dist/icons/x';

import React, { useState } from 'react';

dayjs.extend(isBetween);

function Task(props) {
    return (
        props.editMode === props.id ? <TaskEditor /> :
        <ListGroup.Item className="d-flex align-items-center">
            <Col as="span" className={props.isUrgent && "text-danger font-weight-bold"}>
                <Form.Check
                    custom
                    type="checkbox"
                    id={"custom-checkbox-" + props.id}
                    label={props.description}
                />
            </Col>
            <Col as="span" className="text-dark text-center">{props.isPrivate && <PersonFill size={20} />}</Col>
            <Col as="span" className="font-075 text-right d-flex justify-content-end align-items-center">
                <span>{props.date.format("dddd D MMMM YYYY [at] H:mm")}</span>
                <TaskControls taskID={props.id} deleteTask={props.deleteTask} editTask={props.editTask} />
            </Col>
        </ListGroup.Item>
    );
}

function TaskControls(props) {
    const CustomToggle = React.forwardRef(({ children, onClick }, ref) => (
        <a
            href=""
            ref={ref}
            onClick={(e) => {
                e.preventDefault();
                onClick(e);
            }}
        >
            {children}
        </a>
    ));

    // forwardRef again here!
    // Dropdown needs access to the DOM of the Menu to measure it
    const CustomMenu = React.forwardRef(
        ({ children, style, className, 'aria-labelledby': labeledBy }, ref) => {
            const [value, setValue] = useState('');

            return (
                <div
                    ref={ref}
                    style={style}
                    className={className}
                    aria-labelledby={labeledBy}
                >
                    <ul className="list-unstyled m-0">
                        {React.Children.toArray(children).filter(
                            (child) =>
                                !value || child.props.children.toLowerCase().startsWith(value),
                        )}
                    </ul>
                </div>
            );
        },
    );
    return (
        <Dropdown className="d-inline ml-2">
            <Dropdown.Toggle as={CustomToggle} id="dropdown-custom-components">
                <ThreeDotsVertical color="" size={18} />
            </Dropdown.Toggle>

            <Dropdown.Menu className="min-width-5" as={CustomMenu}>
                <Dropdown.Item className="px-3" onClick={() => props.editTask(props.taskID)}>
                    <PencilSquare className="mr-3" size={16} />
                    Edit
                </Dropdown.Item>
                <Dropdown.Item className="px-3" onClick={() => props.deleteTask(props.taskID)}>
                    <Trash2Fill className="mr-3" size={16} />
                    Delete
                </Dropdown.Item>
            </Dropdown.Menu>
        </Dropdown>
    );
}

function TaskList(props) {
    let newList = applyFilter(props.activeFilter, props.tasks);

    const deleteTask = (tskID) => {
        props.setTasks(tsks => tsks.filter(t => t.id !== tskID));
    }

    const [editMode, setEditMode] = useState(-1);

    const editTask = (tskID) => {
        setEditMode(tskID);
    }

    const taskList = [...newList].map(task => <Task key={task.id} {...task} deleteTask={deleteTask} editTask={editTask} editMode={editMode} />);
    return (
        <ListGroup variant="flush" className="margin-b-75">
            {taskList}
        </ListGroup>
    );
}

function TaskEditor() {
    const task = { id: 1, description: "Fai qualcosa", isPrivate: true, isUrgent: false, date: dayjs("2021-04-26") };
    return (
        <ListGroup.Item className="d-flex align-items-center flex-column flex-md-row">
            <Col as="span">
                <Form.Control type="text" placeholder="Task description"
                    /* value={task.description} */
                />
            </Col>
            <Col as="span" className="d-flex justify-content-md-center my-3">
                <Form.Check
                    custom
                    type="checkbox"
                    id="form-checkbox-1"
                    label="Private"
                    className="mr-3"
                    checked={task.isPrivate}
                />
                <Form.Check
                    custom
                    type="checkbox"
                    id="form-checkbox-2"
                    label="Important"
                    checked={task.isImportant}
                />    
            </Col>
            <Col as="span" className="font-075 text-right d-flex justify-content-end align-items-center">
                <Form.Control type="date"
                    /* value={task.date} */
                />
                <X size={40} className="hover-scaleup cursor-pointer ml-3 mr-1" />
                <Check2 size={48} color="#17a2b8" className="hover-scaleup cursor-pointer" />       
            </Col>            
        </ListGroup.Item>
    );
}

const applyFilter = (selected, list) => {
    switch (selected) {
        case 2:
            return (list.filter(task => task.isUrgent));
        case 3:
            return (list.filter(task => {
                if (dayjs(task.date).format("YYYY-MM-DD") === dayjs().format("YYYY-MM-DD")) return true;
                else return false;
            }));
        case 4:
            return (list.filter(task => {
                if (dayjs(task.date).isBetween(
                    dayjs().startOf('d').add(1, 'day').subtract(1, 'minute'), dayjs().startOf('d').add(8, 'day')
                )) return true;
                else return false;
            }));
        case 5:
            return (list.filter(task => task.isPrivate));
        default:
            return list;
    }
}

export default TaskList;
