import React, { useState } from 'react';
import dayjs from 'dayjs';

import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

import PlusCircleFill from 'react-bootstrap-icons/dist/icons/plus-circle-fill';

function TaskAdder(props) {
    const [show, setShow] = useState(false);
    const [description, setDescription] = useState('');
    const [isPrivate, setIsPrivate] = useState(false);
    const [isImportant, setIsImportant] = useState(false);
    const [date, setDate] = useState('');
    const [validated, setValidated] = useState(false);
    const [dateIsValid, setDateIsValid] = useState();
    const [descIsValid, setDescIsValid] = useState();

    const handleShow = () => setShow(true);
    const handleClose = () => {
        setShow(false);
    };

    const handleSumbit = (event) => {
        const form = event.currentTarget;

        if (form.checkValidity() === false) {
            event.preventDefault();
            event.stopPropagation();
        }

        const id = props.tasks.reduce((prev, current) => (prev.id > current.id) ? prev.id : current.id) + 1;
        const task = { id: id, description: description, isPrivate: isPrivate, isUrgent: isImportant, date: dayjs(date) };

        if (descIsValid && dateIsValid) {
            props.setTasks(tsks => [...tsks, task]);
            handleClose();

            setValidated(false);
            setDescription('');
            setIsPrivate(false);
            setIsImportant(false);
            setDate('');
            setDateIsValid(false);
            setDescIsValid(false);
        }
        else setValidated(true);
    };

    const handleDateChange = (ev) => {
        setDate(ev.target.value);
        setDateIsValid(dayjs(ev.target.value) >= dayjs().startOf('d'));
    };

    const handleDescChange = (ev) => {
        setDescription(ev.target.value);
        setDescIsValid(ev.target.value ? true : false);
    };

    const formProps = {
        description: description, isPrivate: isPrivate, isImportant: isImportant, date: date, validated: validated,
        dateIsValid: dateIsValid, descIsValid: descIsValid,
        setDescription: setDescription, setIsPrivate: setIsPrivate, setIsImportant: setIsImportant,
        handleDateChange: handleDateChange, handleDescChange: handleDescChange
    };

    return (
        <>
            <PlusCircleFill color="#17a2b8" size={64} onClick={handleShow} className="cursor-pointer" />
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Add new task</Modal.Title>
                </Modal.Header>

                <Modal.Body>
                    <TaskForm  {...formProps} />
                </Modal.Body>

                <Modal.Footer>
                    <Button variant="info" onClick={handleSumbit}>Confirm</Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}

function TaskForm(props) {
    return (
        <Form noValidate>
            <Form.Group controlId="formDescription">
                <Form.Label>Task description</Form.Label>
                <Form.Control type="text" placeholder="Enter a description for your task"
                    onChange={(ev) => props.handleDescChange(ev)}
                    isInvalid={!props.validated ? false : !props.descIsValid}
                    isValid={!props.validated ? false : props.descIsValid}
                    value={props.description}
                />
                <Form.Control.Feedback type="invalid">
                    Please add a description.
                </Form.Control.Feedback>
            </Form.Group>
            <Form.Group className="d-flex" controlId="formCheckboxes">
                <Form.Check
                    custom
                    type="checkbox"
                    id="form-checkbox-1"
                    label="Private"
                    className="mr-3"
                    onChange={(ev) => props.setIsPrivate(ev.target.checked)}
                    checked={props.isPrivate}
                />
                <Form.Check
                    custom
                    type="checkbox"
                    id="form-checkbox-2"
                    label="Important"
                    onChange={(ev) => props.setIsImportant(ev.target.checked)}
                    checked={props.isImportant}
                />
            </Form.Group>
            <Form.Group>
                <Form.Label>Deadline</Form.Label>
                <Form.Control type="date" onChange={(ev) => props.handleDateChange(ev)}
                    isInvalid={!props.validated ? false : !props.dateIsValid}
                    isValid={!props.validated ? false : props.dateIsValid}
                    value={props.date}
                />
                <Form.Control.Feedback type="invalid">
                    Deadline should be after current time.
                </Form.Control.Feedback>
            </Form.Group>
        </Form>
    );
}

export default TaskAdder;
