import React, { useState } from 'react';
import dayjs from 'dayjs';

import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';

import PlusCircleFill from 'react-bootstrap-icons/dist/icons/plus-circle-fill';
import Check2 from 'react-bootstrap-icons/dist/icons/check2';
import X from 'react-bootstrap-icons/dist/icons/x';

function TaskAdder(props) {
    const [show, setShow] = useState(false);
    const [description, setDescription] = useState('');
    const [isPrivate, setIsPrivate] = useState(false);
    const [isImportant, setIsImportant] = useState(false);
    const [date, setDate] = useState('');
    const [validated, setValidated] = useState(false);
    const [dateIsValid, setDateIsValid] = useState(false);
    const [descIsValid, setDescIsValid] = useState(false);

    const handleShow = () => setShow(true);
    const handleClose = () => {
        setShow(false);
        setValidated(false);
    };

    const handleSumbit = (event) => {
        event.preventDefault();

        const id = props.tasks.reduce((prev, current) => (prev.id > current.id) ? prev.id : current.id) + 1;
        const task = { id: id, description: description, isPrivate: isPrivate, isUrgent: isImportant, date: dayjs(date) };

        if (descIsValid && dateIsValid) {
            props.setTasks(tsks => [...tsks, task]);
            handleClose();
            clearForm();
        }
        else setValidated(true);
    };

    const handleDateChange = (ev) => {
        setDate(ev.target.value);
        setDateIsValid(dayjs(ev.target.value) >= dayjs().startOf('d'));
    };

    const handleDescChange = (ev) => {
        setDescription(ev.target.value);
        setDescIsValid(ev.target.value ? true : false);
    };

    const clearForm = () => {
        setValidated(false);
        setDescription('');
        setIsPrivate(false);
        setIsImportant(false);
        setDate('');
        setDateIsValid(false);
        setDescIsValid(false);
    };

    const formProps = {
        editMode: props.editMode, editTask: props.editTask,
        description: description, isPrivate: isPrivate, isImportant: isImportant, date: date, validated: validated,
        dateIsValid: dateIsValid, descIsValid: descIsValid,
        setDescription: setDescription, setIsPrivate: setIsPrivate, setIsImportant: setIsImportant,
        handleSumbit: handleSumbit ,handleDateChange: handleDateChange, handleDescChange: handleDescChange
    };

    return (
        <>
            <PlusCircleFill color="#17a2b8" size={64} onClick={handleShow} className="cursor-pointer hover-scaleup" /> 
            {
                props.editMode < 0 ? <TaskForm  {...formProps} /> : 
                <Modal show={show} onHide={handleClose}>
                    <Modal.Header closeButton>
                        <Modal.Title>Add new task</Modal.Title>
                    </Modal.Header>

                    <Modal.Body>
                        <TaskForm  {...formProps} />
                    </Modal.Body>

                    <Modal.Footer>
                        <Button variant="link" className="text-dark" onClick={clearForm}>Clear</Button>
                        <Button variant="info" onClick={handleSumbit}>Confirm</Button>
                    </Modal.Footer>
                </Modal>
            }
        </>
    );
}

function TaskForm2(props) {
    return (
        <Form noValidate onSubmit={props.handleSumbit}>
            <Form.Group controlId="formDescription">
                <Form.Label>Task description</Form.Label>
                <Form.Control type="text" placeholder="Enter a description for your task"
                    onChange={(ev) => props.handleDescChange(ev)}
                    isInvalid={!props.validated ? false : !props.descIsValid}
                    isValid={!props.validated ? false : props.descIsValid}
                    value={props.description}
                />
                <Form.Control.Feedback type="invalid">
                    Please add a description.
                </Form.Control.Feedback>
            </Form.Group>
            <Form.Group className="d-flex" controlId="formCheckboxes">
                <Form.Check
                    custom
                    type="checkbox"
                    id="form-checkbox-1"
                    label="Private"
                    className="mr-3"
                    onChange={(ev) => props.setIsPrivate(ev.target.checked)}
                    checked={props.isPrivate}
                />
                <Form.Check
                    custom
                    type="checkbox"
                    id="form-checkbox-2"
                    label="Important"
                    onChange={(ev) => props.setIsImportant(ev.target.checked)}
                    checked={props.isImportant}
                />
            </Form.Group>
            <Form.Group>
                <Form.Label>Deadline</Form.Label>
                <Form.Control type="date" onChange={(ev) => props.handleDateChange(ev)}
                    isInvalid={!props.validated ? false : !props.dateIsValid}
                    isValid={!props.validated ? false : props.dateIsValid}
                    value={props.date}
                />
                <Form.Control.Feedback type="invalid">
                    Deadline should be after current time.
                </Form.Control.Feedback>
            </Form.Group>
        </Form>
    );
}

function TaskForm(props) {
    const formDescription = <Form.Control type="text" placeholder="Enter a description for your task"
        onChange={(ev) => props.handleDescChange(ev)}
        isInvalid={!props.validated ? false : !props.descIsValid}
        isValid={!props.validated ? false : props.descIsValid}
        value={props.description}
    />

    const formImportant = <Form.Check
        custom
        type="checkbox"
        id="form-checkbox-1"
        label="Important"
        onChange={(ev) => props.setIsimportant(ev.target.checked)}
        checked={props.isImportant}
    />

    const formPrivate = <Form.Check
        custom
        type="checkbox"
        id="form-checkbox-1"
        label="Private"
        className="mr-3"
        onChange={(ev) => props.setIsPrivate(ev.target.checked)}
        checked={props.isPrivate}
    />

    const formDeadline = <Form.Control type="date" onChange={(ev) => props.handleDateChange(ev)}
        isInvalid={!props.validated ? false : !props.dateIsValid}
        isValid={!props.validated ? false : props.dateIsValid}
        value={props.date}
    />

    return (
        props.editMode < 0 ?
        <Form noValidate onSubmit={props.handleSumbit}>
            <Form.Group controlId="formDescription">
                <Form.Label>Task description</Form.Label>
                {formDescription}
                <Form.Control.Feedback type="invalid">
                    Please add a description.
                </Form.Control.Feedback>
            </Form.Group>
            <Form.Group className="d-flex" controlId="formCheckboxes">
                {formPrivate, formImportant}          
            </Form.Group>
            <Form.Group>
                <Form.Label>Deadline</Form.Label>
                {formDeadline}
                <Form.Control.Feedback type="invalid">
                    Deadline should be after current time.
                </Form.Control.Feedback>
            </Form.Group>
        </Form>
        :
        <ListGroup.Item className="d-flex align-items-center flex-column flex-md-row">
            <Col as="span">
                {formDescription}
            </Col>
            <Col as="span" className="d-flex justify-content-md-center my-3">
                {formPrivate, formImportant}
            </Col>
            <Col as="span" className="font-075 text-right d-flex justify-content-end align-items-center">
                {formDeadline}
                <X size={40} className="hover-scaleup cursor-pointer ml-3 mr-1" onClick={() => props.editTask(-1)} />
                <Check2 size={48} color="#17a2b8" className="hover-scaleup cursor-pointer" />       
            </Col>            
        </ListGroup.Item>
    );
}

export default TaskAdder;
