import React, { useState } from 'react';
import dayjs from 'dayjs';

import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

import PlusCircleFill from 'react-bootstrap-icons/dist/icons/plus-circle-fill';

function TaskAdder(props) {
    const baseFormState = {
        description: '', date: dayjs()
    };

    const [show, setShow] = useState(false);
    const [formState, setFormState] = useState(baseFormState);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const handleSumbit = (event) => {
        const form = event.currentTarget;

        if (form.checkValidity() === false) {
            event.preventDefault();
            event.stopPropagation();
        }

        const id = props.tasks.reduce((prev, current) => (prev.id > current.id) ? prev.id : current.id) + 1;
        //const task = { id: id, description: description, isPrivate: isPrivate, isUrgent: isImportant, date: date };

        //props.setTasks(tsks => [...tsks, task]);
        handleClose();
        //setValidated(false);
    };

    const handleChange = (ev) => {
        console.log(ev);
    };

    const formProps = {};

    return (
        <>
            <PlusCircleFill color="#17a2b8" size={64} onClick={handleShow} className="cursor-pointer" />
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Add new task</Modal.Title>
                </Modal.Header>

                <Modal.Body>
                    <TaskForm  {...formProps} />
                </Modal.Body>

                <Modal.Footer>
                    <Button variant="info" onClick={handleSumbit}>Confirm</Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}

function TaskForm(props) {
    return (
        <Form noValidate>
            <Form.Group controlId="formDescription">
                <Form.Label>Task description</Form.Label>
                <Form.Control type="text" placeholder="Enter a description for your task"
                    onChange={false}
                    isInvalid={false}
                    isValid={false}
                />
                <Form.Control.Feedback type="invalid">
                    Please add a description.
                </Form.Control.Feedback>
            </Form.Group>
            <Form.Group className="d-flex" controlId="formCheckboxes">
                <Form.Check
                    custom
                    type="checkbox"
                    id="form-checkbox-1"
                    label="Private"
                    className="mr-3"
                    onChange={false}
                />
                <Form.Check
                    custom
                    type="checkbox"
                    id="form-checkbox-2"
                    label="Important"
                    onChange={false}
                />
            </Form.Group>
            <Form.Group>
                <Form.Label>Deadline</Form.Label>
                <Form.Control type="date" onChange={false}
                    isInvalid={false}
                    isValid={false}
                />
                <Form.Control.Feedback type="invalid">
                    Deadline should be after current time.
                </Form.Control.Feedback>
            </Form.Group>
        </Form>
    );
}

export default TaskAdder;
