import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import ListGroup from 'react-bootstrap/ListGroup';

import PlusCircleFill from 'react-bootstrap-icons/dist/icons/plus-circle-fill';
import Check2 from 'react-bootstrap-icons/dist/icons/check';
import X from 'react-bootstrap-icons/dist/icons/x';

function TaskAdder(props) {
    return (
        <>
            <PlusCircleFill color="#17a2b8" size={64} onClick={props.handleShow} className="cursor-pointer hover-scaleup" />
            <Modal show={props.show} onHide={props.handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Add new task</Modal.Title>
                </Modal.Header>

                <Modal.Body>
                    <TaskForm  {...props.formProps} />
                </Modal.Body>

                <Modal.Footer>
                    <Button variant="link" className="text-dark" onClick={props.clearForm}>Clear</Button>
                    <Button variant="info" onClick={props.handleSumbit}>Confirm</Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}

function TaskForm(props) {
    const formDescription = <Form.Control type="text" placeholder="Enter a description for your task"
        onChange={(ev) => props.handleDescChange(ev)}
        isInvalid={!props.validated ? false : !props.descIsValid}
        isValid={!props.validated ? false : props.descIsValid}
        value={props.description}
    />

    const formImportant = <Form.Check
        custom
        type="checkbox"
        id="form-checkbox-1"
        label="Important"
        onChange={(ev) => props.setIsImportant(ev.target.checked)}
        checked={props.isImportant}
    />

    const formPrivate = <Form.Check
        custom
        type="checkbox"
        id="form-checkbox-2"
        label="Private"
        className="mr-3"
        onChange={(ev) => props.setIsPrivate(ev.target.checked)}
        checked={props.isPrivate}
    />

    const formDeadline = <Form.Control type="date" onChange={(ev) => props.handleDateChange(ev)}
        isInvalid={!props.validated ? false : !props.dateIsValid}
        isValid={!props.validated ? false : props.dateIsValid}
        value={props.date}
    />
    
    return (
        props.editMode < 0 ?
            <Form noValidate onSubmit={props.handleSumbit}>
                <Form.Group controlId="formDescription">
                    <Form.Label>Task description</Form.Label>
                    {formDescription}
                    <Form.Control.Feedback type="invalid">
                        Please add a description.
                </Form.Control.Feedback>
                </Form.Group>
                <Form.Group className="d-flex" controlId="formCheckboxes">
                    {formPrivate} {formImportant}
                </Form.Group>
                <Form.Group>
                    <Form.Label>Deadline</Form.Label>
                    {formDeadline}
                    <Form.Control.Feedback type="invalid">
                        Deadline should be after current time.
                </Form.Control.Feedback>
                </Form.Group>
            </Form>
            :
            <ListGroup.Item>
                <Form noValidate onSubmit={props.handleSumbit} className="d-flex align-items-center flex-column flex-md-row">
                    <Col as="span">
                        {formDescription}
                    </Col>
                    <Col as="span" className="d-flex justify-content-md-center my-3">
                        {formPrivate} {formImportant}
                    </Col>
                    <Col as="span" className="font-075 text-right d-flex justify-content-end align-items-center">
                        {formDeadline}
                        <X size={40} className="hover-scaleup cursor-pointer ml-3 mr-1" onClick={() => props.editTask(-1)} />
                        <Check2 size={44} color="#17a2b8" className="hover-scaleup cursor-pointer" onClick={props.handleSumbit} />
                    </Col>
                </Form>
            </ListGroup.Item>
    );
}

export {TaskAdder, TaskForm};
