import dayjs from 'dayjs';

import {filters, tl} from './data';
import {TaskAdder} from './AdderComponents';
import TaskList from './TaskComponents';

import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';

import React, { useState } from 'react';

function Main(props) {
    const [tasks, setTasks] = useState([...tl]);
    const [editMode, setEditMode] = useState(-1);
    const editTask = (tskID) => {
        const currentTask = tasks.find(task => task.id === tskID);

        setDescription(currentTask.description);
        setIsPrivate(currentTask.isPrivate);
        setIsImportant(currentTask.isUrgent);
        setDate(currentTask.date);

        setEditMode(tskID);
    };

    const [show, setShow] = useState(false);
    const [description, setDescription] = useState('');
    const [isPrivate, setIsPrivate] = useState(false);
    const [isImportant, setIsImportant] = useState(false);
    const [date, setDate] = useState('');
    const [validated, setValidated] = useState(false);
    const [dateIsValid, setDateIsValid] = useState(false);
    const [descIsValid, setDescIsValid] = useState(false);
    
    const handleShow = () => {
        setEditMode(-1);
        setShow(true);
    }
    const handleClose = () => {
        setShow(false);
        setValidated(false);
    };

    const handleSumbit = (event) => {
        event.preventDefault();

        const id = tasks.reduce((prev, current) => (prev.id > current.id) ? prev.id : current.id) + 1;
        const task = { id: id, description: description, isPrivate: isPrivate, isUrgent: isImportant, date: dayjs(date) };

        if (descIsValid && dateIsValid) {
            setTasks(tsks => [...tsks, task]);
            handleClose();
            clearForm();
        }
        else setValidated(true);
    };

    const handleDateChange = (ev) => {
        setDate(ev.target.value);
        setDateIsValid(dayjs(ev.target.value) >= dayjs().startOf('d'));
    };

    const handleDescChange = (ev) => {
        setDescription(ev.target.value);
        setDescIsValid(ev.target.value ? true : false);
    };

    const clearForm = () => {
        setValidated(false);
        setDescription('');
        setIsPrivate(false);
        setIsImportant(false);
        setDate('');
        setDateIsValid(false);
        setDescIsValid(false);
    };   

    const formProps = {
        editMode: editMode, editTask: editTask,
        description: description, isPrivate: isPrivate, isImportant: isImportant, date: date, validated: validated,
        dateIsValid: dateIsValid, descIsValid: descIsValid,
        setDescription: setDescription, setIsPrivate: setIsPrivate, setIsImportant: setIsImportant,
        handleSumbit: handleSumbit, handleDateChange: handleDateChange, handleDescChange: handleDescChange
    };

    const adderProps = {
        tasks: tasks, setTasks: setTasks, editMode: editMode, editTask: editTask, formProps: formProps,
        show: show, handleShow: handleShow, handleClose: handleClose, handleSumbit: handleSumbit, clearForm: clearForm, 
    };

    return (
        <Col as="main" lg={8} className="py-3"><h1>{filters[props.activeFilter - 1].text}</h1>
            <TaskList activeFilter={props.activeFilter} tasks={tasks} setTasks={setTasks} editMode={editMode} editTask={editTask} formProps={formProps} />
            <Container fluid className="fixed-bottom d-flex flex-row-reverse px-4 mb-4">
                <TaskAdder {...adderProps} />
            </Container>
        </Col>
    );
}

export default Main;
